mapper.select:
SELECT JOBTALKSEQ, MEMBERID, JOBTALK_REF, JOBTALK_TITLE, JOBTALK_CONTENT, WDATE, DEL, JOBTALK_READCOUNT FROM (SELECT ROW_NUMBER()OVER(ORDER BY JOBTALK_REF DESC) AS RNUM, JOBTALKSEQ, MEMBERID, JOBTALK_REF, JOBTALK_TITLE, JOBTALK_CONTENT, WDATE, DEL, JOBTALK_READCOUNT FROM JOBTALK WHERE 1=1 AND DEL = 0
AND JOBTALK_TITLE LIKE '%'||#{search}||'%'
AND JOBTALK_CONTENT LIKE '%'||#{search}||'%'
AND MEMBERID=#{search}
ORDER BY JOBTALK_REF DESC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT JOBTALKSEQ, MEMBERID, JOBTALK_REF, JOBTALK_TITLE, JOBTALK_CONTENT, WDATE, DEL, JOBTALK_READCOUNT 
FROM JOBTALK
WHERE JOBTALKSEQ=#{jobtalkseq}

mapper.update:
UPDATE JOBTALK
SET JOBTALK_READCOUNT=JOBTALK_READCOUNT+1
WHERE JOBTALKSEQ=#{jobtalkseq}

UPDATE JOBTALK
SET DEL=1
WHERE JOBTALKSEQ=#{jobtalkseq}

UPDATE JOBTALK SET
JOBTALK_TITLE=#{jobtalk_title}, JOBTALK_CONTENT=#{jobtalk_content}, WDATE=SYSDATE
WHERE JOBTALKSEQ=#{jobtalkseq}
