mapper.update:
UPDATE JOB

SET JOBTITLE=#{dto.jobTitle} , JOBCONTENT=#{dto.jobContent} , AREA1NAME=#{dto.area1Name}, AREA2NAME=#{dto.area2Name},
         JOBVOLUMN=#{dto.jobVolumn}, SALARY=#{dto.salary}, JOBSTART=TO_DATE(#{dto.jobStart}, 'YYYY-MM-DD HH24:MI:SS'), JOBEND=TO_DATE(#{dto.jobEnd}, 'YYYY-MM-DD HH24:MI:SS'),
         EMP_TYPE=#{dto.emp_Type}, EDUCATION=#{dto.education} , CAREER_TYPE=#{dto.career_Type}



WHERE JOBSEQ = #{dto.jobSeq}

mapper.select:
SELECT COUNT(DISTINCT JOBSEQ)
FROM (SELECT ROW_NUMBER()OVER(ORDER BY JOBSEQ DESC)AS RNUM, J.JOBSEQ , J.COMPANYID , J.JOBTITLE , J.JOBCONTENT , J.AREA1NAME, J.AREA2NAME, J.JOBVOLUMN, J.SALARY, J.JOBSTART, J.JOBEND, J.EMP_TYPE, J.EDUCATION , J.CAREER_TYPE, J.CAREER_DESC, J.READCOUNT, J.REGDATE, J.BUSCODE, J.DEL, C.COMPANYNAME FROM JOB J, BUSINESS C WHERE J.COMPANYID = C.MEMBERID
AND J.JOBTITLE LIKE '%'||#{search}||'%'
AND C.COMPANYNAME LIKE '%'||#{search}||'%'
)J
JOIN BUSINESS C ON J.COMPANYID = C.MEMBERID
JOIN EDUCATION E ON E.EDUCODE = J.EDUCATION
JOIN EMPLOYEE_TYPE T ON T.EMP_TYPE = J.EMP_TYPE
JOIN JOB_CAREERTYPE P ON P.CAREER_TYPE = J.CAREER_TYPE
JOIN bscodstack K ON J.JOBSEQ = K.JOBSEQ WHERE 1=1
AND J.EDUCATION <= ${education}
AND j.career_type >= ${param.careerStart}
AND j.career_type <= ${param.careerEnd}
AND j.career_type BETWEEN ${param.careerStart} AND ${param.careerEnd}
J.AREA2NAME = #{item}
K.RBUSCODE = #{item}
ORDER BY J.JOBSEQ DESC

SELECT DISTINCT RNUM, J.JOBSEQ , J.COMPANYID , J.JOBTITLE , J.JOBCONTENT , J.AREA1NAME, J.AREA2NAME, J.JOBVOLUMN, J.SALARY, J.JOBSTART, J.JOBEND, J.EMP_TYPE, J.EDUCATION , J.CAREER_TYPE, J.READCOUNT, J.REGDATE, J.BUSCODE, J.DEL, C.COMPANYNAME, E.EDUNAME, T.EMP_NAME, P.CAREER_DESC
FROM (SELECT ROW_NUMBER()OVER(ORDER BY JOBSEQ DESC)AS RNUM, J.JOBSEQ , J.COMPANYID , J.JOBTITLE , J.JOBCONTENT , J.AREA1NAME, J.AREA2NAME, J.JOBVOLUMN, J.SALARY, J.JOBSTART, J.JOBEND, J.EMP_TYPE, J.EDUCATION , J.CAREER_TYPE, J.CAREER_DESC, J.READCOUNT, J.REGDATE, J.BUSCODE, J.DEL, C.COMPANYNAME FROM JOB J, BUSINESS C WHERE J.COMPANYID = C.MEMBERID
AND J.JOBTITLE LIKE '%'||#{search}||'%'
AND C.COMPANYNAME LIKE '%'||#{search}||'%'
)J
JOIN BUSINESS C ON J.COMPANYID = C.MEMBERID
JOIN EDUCATION E ON E.EDUCODE = J.EDUCATION
JOIN EMPLOYEE_TYPE T ON T.EMP_TYPE = J.EMP_TYPE
JOIN JOB_CAREERTYPE P ON P.CAREER_TYPE = J.CAREER_TYPE
JOIN bscodstack K ON J.JOBSEQ = K.JOBSEQ WHERE 1=1
AND J.EDUCATION <= ${education}
AND j.career_type >= ${param.careerStart}
AND j.career_type <= ${param.careerEnd}
AND j.career_type BETWEEN ${param.careerStart} AND ${param.careerEnd}
J.AREA2NAME = #{item}
K.RBUSCODE = #{item}
AND RNUM BETWEEN ${param.start} AND ${param.end} ORDER BY J.JOBSEQ DESC

SELECT BUSCODE
FROM BSCODSTACK
WHERE JOBSEQ = ${jobseq}

mapper.update:
UPDATE JOB
SET READCOUNT = READCOUNT +1
WHERE JOBSEQ = #{jobseq}

UPDATE JOB
SET DEL=1
WHERE JOBSEQ=#{jobseq}

UPDATE MGDETAIL
SET detailAdress1 = #{detailAdress1}, detailAdress2 = #{detailAdress2}, mgName = #{mgName}, mgPhone = #{mgPhone}, mgEmail = #{mgEmail}
WHERE JOBSEQ=#{jobseq}
