insert.if:
DECLARE

jSeq number(10);
memid varchar2(50);
exist number(10);

BEGIN

jSeq := #{jobSeq};
memid := #{memberid};

select count(*) 
into exist
from JOBFAVORITE
WHERE JOBSEQ = jSeq and  memberid = memid;

IF exist<=0 then
INSERT INTO JOBFAVORITE (JFSEQ, JOBSEQ, MEMBERID)
		VALUES(JOBFAVORITE_SEQ.NEXTVAL, jSeq, memid);

ELSE

dbms_output.put_line('이미 즐겨찾기 하셨습니다');

END IF;

END;

mapper.delete:
   DELETE FROM JOBFAVORITE
  WHERE JOBSEQ = #{jobSeq} and 
		MEMBERID = #{memberid}

insert.if:

DECLARE

comId varchar2(50);
memid varchar2(50);
existCom number(10);

BEGIN

comId := #{companyId};
memid := #{memberid};

select count(*) 
into existCom
from COMFAVORITE
WHERE COMPANYID = comId and  MEMBERID = memid;

IF existCom<=0 then
INSERT INTO COMFAVORITE (CFSEQ, COMPANYID, MEMBERID)
		VALUES(COMFAVORITE_SEQ.NEXTVAL, comId, memid);

ELSE

dbms_output.put_line('이미 즐겨찾기 하셨습니다');

END IF;

END;

mapper.delete:
   DELETE FROM COMFAVORITE
  WHERE COMPANYID = #{companyId} and 
		MEMBERID = #{memberid}

mapper.update:
 	 UPDATE BUSINESS
	 SET ISPREMIUM = 1 
	 WHERE MEMBERID = #{memberid}
