mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT FROM (SELECT ROW_NUMBER()OVER(ORDER BY REF DESC, STEP ASC) AS RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT FROM JOBTALK WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND ID=#{search}
ORDER BY REF DESC, STEP ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT 
FROM JOBTALK
WHERE SEQ=#{seq}

mapper.update:
UPDATE JOBTALK
SET READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

UPDATE JOBTALK
SET DEL=1
WHERE SEQ=#{seq}

UPDATE JOBTALK SET
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE
WHERE SEQ=#{seq}
