mapper.select:
SELECT NOTICESEQ, MEMBERID, TITLE,CONTENT, FILENAME, NEWFILENAME, READCOUNT, DOWNCOUNT, REGDATE,NOTICE_REF, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY NOTICE_REF DESC) AS RNUM, NOTICESEQ, MEMBERID, TITLE, CONTENT, FILENAME, NEWFILENAME, READCOUNT, DOWNCOUNT,REGDATE, NOTICE_REF, DEL FROM NOTICE WHERE DEL=0
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND MEMBERID=#{search}
ORDER BY NOTICE_REF DESC) WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE NOTICE
SET DEL=1
WHERE NOTICESEQ=#{noticeseq}

mapper.select:
SELECT NOTICESEQ, MEMBERID, TITLE, CONTENT, FILENAME, NEWFILENAME, READCOUNT, DOWNCOUNT, REGDATE, NOTICE_REF, DEL
FROM NOTICE
WHERE NOTICESEQ=#{noticeseq}

mapper.update:
UPDATE NOTICE
SET READCOUNT=READCOUNT+1
WHERE NOTICESEQ=#{noticeseq}

UPDATE NOTICE
SET TITLE=#{title}, CONTENT=#{content}, FILENAME=#{filename}, NEWFILENAME=#{newfilename}, REGDATE=SYSDATE
WHERE NOTICESEQ=#{noticeseq}
