mapper.select:
SELECT RESUMESEQ
FROM RESUME
WHERE RESUMETITLE = #{resumetitle}

SELECT RESUMESEQ, MEMBERID, RESUMETITLE, RECRUIT_STATUS, RESUMEIMAGE, NEWRESUMEIMAGE, RESUME_INTRO_TITLE, RESUME_INTRO_CONTENT, PORTFOLIO, NEWPORTFOLIO, DESIREDSALARY, DESIREDJOBTYPE, DESIRED_STR, DESIRED_END, DESIREDAREA1, DESIREDJOB1, GENDER
FROM RESUME
WHERE RESUMESEQ=#{seq}

SELECT CAREERSEQ, RESUMESEQ, PRE_COMNAME, PRE_STARTDATE, PRE_ENDDATE, PRE_STATUS, PRE_POSITION, PRE_BUSCODE, PRE_AREA, PRE_DEPT, PRE_SAL, PRE_JOBDETAIL
FROM CAREER
WHERE RESUMESEQ=#{seq}

SELECT LICSEQ, RESUMESEQ, LIC_DATE, LIC_NAME, LIC_PUBLISHER, LIC_PASS
FROM LICENSE
WHERE RESUMESEQ=#{seq}

SELECT ACTIVITYSEQ, RESUMESEQ, ACT_FIELD, ACT_ORG, ACT_STR, ACT_END, ACT_DETAIL
FROM ACTIVITY
WHERE RESUMESEQ=#{seq}

SELECT AWDSEQ, RESUMESEQ, AWD_NAME, AWD_DATE, AWD_ORG
FROM AWARD
WHERE RESUMESEQ=#{seq}

SELECT LANSEQ, RESUMESEQ, LAN_KIND, LAN_EXAM, LAN_SCORE, LAN_GRADE, LAN_PASS, LAN_DATE
FROM LANGUAGE
WHERE RESUMESEQ=#{seq}
