mapper.select:
SELECT NVL(COUNT(*), 0)
FROM BUSINESS
WHERE MEMBERID=#{memberid}

SELECT MEMBERID, PWD, COMPANYNAME, NAME, AUTH , COMADDRESS, NEWFILENAME , EMAIL
FROM BUSINESS
WHERE MEMBERID=#{memberid} AND PWD=#{pwd}

SELECT MEMBERID,COMPANYNAME,PWD,COMADDRESS,ADDRESSDETAIL,AUTH,NAME,FILENAME, NEWFILENAME
FROM BUSINESS
WHERE MEMBERID=#{memberid}

mapper.update:
UPDATE BUSINESS
SET MEMBERID=#{memberid}, COMPANYNAME=#{companyname},COMADDRESS=#{comaddress}, ADDRESSDETAIL=#{addressdetail}, PWD=#{pwd}, FILENAME=#{filename}, NEWFILENAME=#{newfilename}
WHERE MEMBERID=#{memberid}
