mapper.select:
SELECT J.RNUM, J.JOBSEQ , J.COMPANYID , J.JOBTITLE , J.JOBCONTENT , J.AREA1NAME, J.AREA2NAME, J.JOBVOLUMN, J.SALARY, J.JOBSTART, J.JOBEND, J.EMP_TYPE, J.EDUCATION , J.CAREER_TYPE, J.CAREER_DESC, J.READCOUNT, J.REGDATE, J.BUSCODE, J.DEL, C.COMPANYNAME FROM COMPANY C JOIN (SELECT ROW_NUMBER()OVER(ORDER BY JOBSEQ DESC)AS RNUM, J.JOBSEQ , J.COMPANYID , J.JOBTITLE , J.JOBCONTENT , J.AREA1NAME, J.AREA2NAME, J.JOBVOLUMN, J.SALARY, J.JOBSTART, J.JOBEND, J.EMP_TYPE, J.EDUCATION , J.CAREER_TYPE, J.CAREER_DESC, J.READCOUNT, J.REGDATE, J.BUSCODE, J.DEL, C.COMPANYNAME FROM JOB J, COMPANY C WHERE J.COMPANYID = C.COMPANYID AND J.DEL =0
AND J.JOBTITLE LIKE '%'||#{search}||'%'
AND C.COMPANYNAME LIKE '%'||#{search}||'%'
)J ON J.COMPANYID = C.COMPANYID WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE JOB
SET READCOUNT = READCOUNT +1 
WHERE JOBSEQ = #{jobseq}

UPDATE JOB 
SET DEL=1 
WHERE SEQ=#{jobseq}
